'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "BILL" ("PURCHASE_ORDER", "M_QUEUE_NO", "M_TABLE_NO", "BILL_STATUS", "REQUEST_CHECK_BILL", "ZONE", "NAME_ZONE", "TABLE_NO") AS ',
'  SELECT a."PURCHASE_ORDER",a."M_QUEUE_NO",a."M_TABLE_NO",a."BILL_STATUS",a."REQUEST_CHECK_BILL",b.zone,c.name_zone,b.table_no',
'from PURCHASE_ORDER_MENU a , T_table b,z_zone c',
'WHERE ',
'    a.bill_status = ''0'' and a.REQUEST_CHECK_BILL = 1 and',
'    b.Table_id = a.M_table_no and',
'    c.zone_id=b.zone',
' order by a.BILL_STATUS;',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "BILL_LIST" ("PURCHASE_ORDER", "M_QUEUE_NO", "M_TABLE_NO", "BILL_STATUS", "REQUEST_CHECK_BILL", "FOOD_NAME", "MENU_AMOUNT", "ZONE", "NAME_ZONE", "TABLE_NO") AS ',
'  SELECT a."PURCHASE_ORDER",a."M_QUEUE_NO",a."M_TABLE_NO",a."BILL_STATUS",a."REQUEST_CHECK_BILL", e.food_name, b.menu_amount, c.zone, d.name_zone, c.table_no',
'FROM PURCHASE_ORDER_MENU a',
'JOIN menu_list b ON b.M_PURCHASE_ORDER = a.PURCHASE_ORDER',
'JOIN t_table c ON c.table_id = a.M_TABLE_NO',
'JOIN z_zone d ON d.zone_id = c.zone',
'JOIN food_list e ON e.food_id = b.M_FOOD_ID',
'WHERE a.bill_status = ''0'' AND a.REQUEST_CHECK_BILL = 1',
'ORDER BY a.PURCHASE_ORDER, a.bill_status, a.REQUEST_CHECK_BILL',
';',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "EBA_DEMO_SEARCH_CUST_ORD_PRD" ("ORDER_ID", "ORDER_STATUS", "ORDER_DATETIME", "CUSTOMER_ID", "EMAIL_ADDRESS", "FULL_NAME", "ORDER_TOTAL", "ITEMS") AS ',
'  select o.order_id,',
'      o.order_status,',
'      o.order_datetime,',
'      c.customer_id,',
'      c.email_address,',
'      c.full_name,',
'      sum ( oi.quantity * oi.unit_price ) order_total,',
'      listagg (p.product_name, '', '' on overflow truncate ''...'' with count)   ',
'         within group ( order by oi.line_item_id ) items  ',
'from eba_demo_search_orders o  ',
'join eba_demo_search_order_items oi ',
'  on o.order_id = oi.order_id  ',
'join eba_demo_search_customers c',
'  on o.customer_id = c.customer_id  ',
'join eba_demo_search_products p',
'  on oi.product_id = p.product_id',
'group by o.order_id,',
'        o.order_datetime,',
'        o.order_status,',
'        c.customer_id,',
'        c.email_address,',
'        c.full_name',
';',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "EBA_DEMO_SEARCH_PRD_ORDERS" ("PRODUCT_NAME", "ORDER_STATUS", "TOTAL_SALES", "ORDER_COUNT") AS ',
'  select p.product_name,',
'       o.order_status,',
'       sum ( oi.quantity * oi.unit_price ) total_sales,',
'       count (*) order_count ',
'from eba_demo_search_orders o  ',
'join eba_demo_search_order_items oi ',
'  on o.order_id = oi.order_id  ',
'join eba_demo_search_customers c',
'  on o.customer_id = c.customer_id  ',
'join eba_demo_search_products p',
'  on oi.product_id = p.product_id',
'group by p.product_name  ',
',        o.order_status',
';',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "EBA_DEMO_SEARCH_PRD_REVIEWS" ("PRODUCT_NAME", "RATING", "AVG_RATING", "REVIEW") AS ',
'  select p.product_name,',
'       r.rating,',
'       round (avg (r.rating) over (partition by product_name), 2) avg_rating,',
'       r.review',
'from eba_demo_search_products p,',
'    json_table  ',
'       (p.product_details, ''$.reviews[*]''  ',
'        columns (',
'            rating number path ''$.rating'',',
'            review varchar2(4000) path ''$.review'')  ',
'       ) r',
';',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "EBA_DEMO_SEARCH_STR_ORDERS" ("TOTAL", "STORE_NAME", "ADDRESS", "LATITUDE", "LONGITUDE", "ORDER_STATUS", "ORDER_COUNT", "TOTAL_SALES") AS ',
'  select case  ',
'         grouping_id ( store_name, order_status )  ',
'           when 1 then ''STORE TOTAL''  ',
'           when 2 then ''STATUS TOTAL''  ',
'           when 3 then ''GRAND TOTAL''  ',
'       end total,',
'	         s.store_name,',
'	         coalesce ( s.web_address, s.physical_address ) address,',
'	         s.latitude,',
'	         s.longitude,',
'	         o.order_status,',
'	         count ( distinct o.order_id ) order_count,',
'	         sum ( oi.quantity * oi.unit_price ) total_sales  ',
'from eba_demo_search_stores s  ',
'join eba_demo_search_orders o ',
'  on s.store_id = o.store_id ',
'join eba_demo_search_order_items oi ',
'  on o.order_id = oi.order_id ',
'group by grouping sets (  (s.store_name, coalesce(s.web_address,s.physical_address), s.latitude, s.longitude) ,',
'							 (s.store_name, coalesce(s.web_address,s.physical_address), s.latitude, s.longitude, o.order_status),',
'							  o.order_status ,',
'							 ()  ',
'                       )',
';',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "EBA_DEMO_SEARCH_STR_ORD_STAT" ("STORE_NAME", "ADDRESS", "LATITUDE", "LONGITUDE", "ORDER_STATUS", "ORDER_COUNT", "TOTAL_SALES") AS ',
'  select s.store_name,',
'        coalesce ( s.web_address, s.physical_address ) address,',
'        s.latitude,',
'        s.longitude,',
'        o.order_status,',
'        count ( distinct o.order_id ) order_count,',
'        sum ( oi.quantity * oi.unit_price ) total_sales ',
'from eba_demo_search_stores s',
'join eba_demo_search_orders o ',
'  on s.store_id = o.store_id',
'join eba_demo_search_order_items oi ',
'  on o.order_id = oi.order_id',
'group by  s.store_name, coalesce ( s.web_address, s.physical_address ), s.latitude, s.longitude, o.order_status',
';',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "MENU_WAITING_LIST" ("ORDER_NO", "M_FOOD_ID", "MENU_AMOUNT", "RATE", "TOTAL", "M_PURCHASE_ORDER", "COOK_STATUS", "FOOD_NAME", "M_TABLE_NO", "ZONE", "NAME_ZONE", "TABLE_NO") AS ',
'  select a."ORDER_NO",a."M_FOOD_ID",a."MENU_AMOUNT",a."RATE",a."TOTAL",a."M_PURCHASE_ORDER",a."COOK_STATUS",e.FOOD_NAME, b.M_TABLE_NO,c.ZONE,d.NAME_ZONE,c.TABLE_NO',
'from MENU_LIST a,PURCHASE_ORDER_MENU b,T_TABLE c,Z_ZONE d,FOOD_LIST e',
'where cook_status is null and',
'      c.TABLE_ID=b.M_TABLE_NO and ',
'      d.ZONE_ID=c.ZONE and',
'      b.PURCHASE_ORDER=a.M_PURCHASE_ORDER and',
'      e.FOOD_ID=a.M_FOOD_ID',
' order by a.M_PURCHASE_ORDER,a.ORDER_NO',
';',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "SUMMARY_ORDER_DETAIL_V" ("DATE_QUEUE", "M_FOOD_ID", "FOOD_NAME", "AMOUNT") AS ',
'  (select c.date_queue ,',
'm_food_id,(select food_name',
'                  from food_list',
'                  where food_id=a.m_food_id) food_name,sum(menu_amount) amount',
'from menu_list a,purchase_order_menu b,q_queue c',
'where a.m_purchase_order=b.purchase_order and',
'       c.queue_no=b.m_queue_no',
'group by date_queue,m_food_id);',
'',
'  CREATE OR REPLACE FORCE EDITIONABLE VIEW "V_MATERIAL_USE_IN_MENU" ("FOOD_ID", "FOOD_NAME", "FOOD_PRICE", "M_TYPE_ID", "USE_ID", "M_MATERIAL_ID", "LIST_AMOUNT", "MATERIAL_NAME") AS ',
'  (select ',
'a.FOOD_ID,a.FOOD_NAME,a.FOOD_PRICE,a.M_TYPE_ID,',
'b.use_id,b.M_MATERIAL_ID,b.LIST_AMOUNT,',
'(select material_name ',
'            from m_material',
'            where material_id=b.m_material_id) material_name ',
'from food_list a, material_use_menu b',
'where a.food_id=b.m_food_id);',
