,p_display_name=>'APEX QR Code Scanner'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('REGION TYPE','APEX.QR.CODE.SCANNER'),'')
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'FUNCTION F_RENDER (',
'    P_REGION                IN APEX_PLUGIN.T_REGION,',
'    P_PLUGIN                IN APEX_PLUGIN.T_PLUGIN,',
'    P_IS_PRINTER_FRIENDLY   IN BOOLEAN',
') RETURN APEX_PLUGIN.T_REGION_RENDER_RESULT IS',
'BEGIN',
'    APEX_JAVASCRIPT.ADD_LIBRARY(',
'        P_NAME        => ''qrcodescanner.pkg.min'',',
'        P_DIRECTORY   => P_PLUGIN.FILE_PREFIX,',
'        P_VERSION     => NULL,',
'        P_KEY         => ''qrscanjssrc''',
'    );',
'',
'    APEX_JAVASCRIPT.ADD_ONLOAD_CODE( ''qrCodeScanner.initialize(''',
'       || APEX_JAVASCRIPT.ADD_VALUE( P_REGION.STATIC_ID, TRUE )',
'       || APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_01, TRUE )',
'       || APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_02, TRUE )',
'       || APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_03, TRUE )',
'       || APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_04, FALSE )',
'       || '');'' );',
'',
'    RETURN NULL;',
'END;'))
,p_api_version=>1
,p_render_function=>'F_RENDER'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>wwv_flow_string.join(wwv_flow_t_varchar2(
'This Region Plug-in is used to scan QR Codes. If any string has been detected an APEX Item can be set, Dynamic Action can be fired or JavaScript can be executed.',
'',
'To control the QR Code Scanner you can fire events on th QR Code region.',
'',
'The following events are supportet:',
'',
'$("#qrscanner_region").trigger("scannerPause"); => Pause the scanner',
'$("#qrscanner_region").trigger("scannerPlay"); => Restart the scanner from pause',
'$("#qrscanner_region").trigger("resetValue"); => Reset value to rescan the same QR Code if needed',
'',
'Login for Demo is: user-demo / 123456@'))
,p_version_identifier=>'1.4'
